import React, { useState } from 'react';
import { questions, quizResultDescriptions } from './data';
import { useNavigate } from 'react-router-dom';
import './QuizPage.css';

const QuizPage = () => {
  const navigate = useNavigate();
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  // 假设每题仅允许单选，answers 存放题目 id 对应的选项索引
  const [answers, setAnswers] = useState({});
  const [result, setResult] = useState(null);

  const handleOptionClick = (questionId, optionIndex) => {
    setAnswers({ ...answers, [questionId]: optionIndex });
  };

  const goToNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const goToPreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // 检查每题是否都回答了
    for (let i = 0; i < questions.length; i++) {
      if (answers[questions[i].id] === undefined) {
        alert(`Please select an option for question ${i + 1}`);
        return;
      }
    }
    // 示例：计算得分
    const scores = {
      Squirrel: 0,
      Penguin: 0,
      Owl: 0,
      Fox: 0,
      Raccoon: 0,
      Cheetah: 0,
      'Golden Retriever': 0,
      Capybara: 0,
    };

    questions.forEach((question) => {
      const selectedIndex = answers[question.id];
      if (selectedIndex !== undefined) {
        const selectedOption = question.options[selectedIndex];
        for (let animal in selectedOption.score) {
          scores[animal] += selectedOption.score[animal];
        }
      }
    });

    let finalAnimal = null;
    let highestScore = -Infinity;
    Object.keys(scores).forEach((animal) => {
      if (scores[animal] > highestScore) {
        highestScore = scores[animal];
        finalAnimal = animal;
      }
    });

    const finalResult = quizResultDescriptions[finalAnimal];
    setResult(finalResult);
  };

  const currentQuestion = questions[currentQuestionIndex];
  // 如果选项数量为5，使用 grid-5 类；否则使用 grid-4（均采用两列布局）
  const gridClass =
    currentQuestion.options.length === 5 ? 'options-grid grid-5' : 'options-grid grid-4';

  return (
    <div className="quiz-page">
      {/* 内容区域下移以留出 Navbar 空间（假设 Navbar 在 App.js 中引入） */}
      <div className="quiz-content">
        <div className="question-header">
          <div className="question-number-container">
            <div className="circle-background"></div>
            <div className="circle-border"></div>
            <div className="question-number-text">
              #{currentQuestionIndex + 1}
            </div>
          </div>
          <div className="question-text">
            {currentQuestion.question}
          </div>
        </div>

        <div className={gridClass}>
          {currentQuestion.options.map((option, index) => (
            <div
              key={index}
              className={`option-card ${answers[currentQuestion.id] === index ? 'selected' : ''}`}
              onClick={() => handleOptionClick(currentQuestion.id, index)}
            >
              <div className="option-text">{option.option}</div>
            </div>
          ))}
        </div>

        <div className="nav-buttons">
          {currentQuestionIndex > 0 && (
            <button type="button" className="nav-btn" onClick={goToPreviousQuestion}>
              &larr; Previous
            </button>
          )}
          {currentQuestionIndex < questions.length - 1 ? (
            <button type="button" className="nav-btn" onClick={goToNextQuestion}>
              Next &rarr;
            </button>
          ) : (
            <button type="button" className="submit-btn" onClick={handleSubmit}>
              Submit Quiz
            </button>
          )}
        </div>

        <div className="progress-bar">
          {questions.map((q, idx) => (
            <div
              key={idx}
              className={`progress-indicator ${idx === currentQuestionIndex ? 'active' : ''}`}
            >
              {idx + 1}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default QuizPage;
