import React, { useState } from 'react';
import { questions, quizResultDescriptions } from './data';
import './QuizPage.css';

const QuizPage = () => {

  // 当前题目索引（0 ~ questions.length-1）
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

  // 每道题最多可选2个选项：answers[questionId] = [optionIndex1, optionIndex2]
  const [answers, setAnswers] = useState({});

  // 最终结果（若为 null 则尚未生成）
  const [result, setResult] = useState(null);

  // 倒计时相关
  const [isCountingDown, setIsCountingDown] = useState(false);
  const [countdown, setCountdown] = useState(3);

  // 点击选项：若已选中则取消，若未选中则添加，最多2个
  const handleOptionClick = (questionId, optionIndex) => {
    const selectedOptions = answers[questionId] || [];
    if (selectedOptions.includes(optionIndex)) {
      // 取消选中
      const newSelection = selectedOptions.filter(i => i !== optionIndex);
      setAnswers({ ...answers, [questionId]: newSelection });
    } else {
      // 新选中
      if (selectedOptions.length < 2) {
        setAnswers({
          ...answers,
          [questionId]: [...selectedOptions, optionIndex],
        });
      } else {
        alert('You can select at most 2 options for this question.');
      }
    }
  };

  // 下一题
  const goToNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  // 上一题
  const goToPreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  // 开始倒计时
  const startCountdown = (finalResult) => {
    setIsCountingDown(true);
    setCountdown(3);
    const interval = setInterval(() => {
      setCountdown((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          setIsCountingDown(false);
          setResult(finalResult);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  // 提交测验
  const handleSubmit = (e) => {
    e.preventDefault();

    // 1) 检查是否每题至少选1个
    for (let i = 0; i < questions.length; i++) {
      const q = questions[i];
      const selected = answers[q.id] || [];
      if (selected.length === 0) {
        alert(`Please select at least one option for question ${i + 1}`);
        return;
      }
    }

    // 2) 计算分数
    const scores = {
      Squirrel: 0,
      Penguin: 0,
      Owl: 0,
      Fox: 0,
      Raccoon: 0,
      Cheetah: 0,
      'Golden Retriever': 0,
      Capybara: 0,
    };

    questions.forEach((question) => {
      const selectedIndices = answers[question.id] || [];
      selectedIndices.forEach((idx) => {
        const selectedOption = question.options[idx];
        for (let animal in selectedOption.score) {
          scores[animal] += selectedOption.score[animal];
        }
      });
    });

    // 3) 找到最高分；并列则取最先出现的
    let finalAnimal = null;
    let highestScore = -Infinity;
    for (const animal of Object.keys(scores)) {
      if (scores[animal] > highestScore) {
        highestScore = scores[animal];
        finalAnimal = animal;
      }
    }

    const finalResult = quizResultDescriptions[finalAnimal];
    startCountdown(finalResult);
  };

  // 重做测验
  const handleRetake = () => {
    setCurrentQuestionIndex(0);
    setAnswers({});
    setResult(null);
  };

  // 查看更多金融人格
  const handleLearnMore = () => {
    navigate('/personality');
  };

  // 当前题目
  const question = questions[currentQuestionIndex];
  const questionId = question.id;
  // 当前题目选项数组
  const optionCount = question.options.length;

  // 判断是否有5个选项，需要两列三行的布局
  // 我们在 CSS 中只提供 4 个选项的绝对定位 (2×2)
  // 当有第5个选项时，额外放在第三行左侧
  const getOptionPositionClass = (index) => {
    if (optionCount === 4) {
      // 与原本 2×2 布局一致
      if (index === 0) return 'option-box option-box-1';
      if (index === 1) return 'option-box option-box-2';
      if (index === 2) return 'option-box option-box-3';
      if (index === 3) return 'option-box option-box-4';
    } else if (optionCount === 5) {
      // 前4个位置与 2×2 一致，第5个位置放第三行左侧
      if (index === 0) return 'option-box option-box-1';
      if (index === 1) return 'option-box option-box-2';
      if (index === 2) return 'option-box option-box-3';
      if (index === 3) return 'option-box option-box-4';
      if (index === 4) return 'option-box option-box-5'; // 在CSS中定义新定位
    }
    // 如果有其他数量（6、7...）可继续扩展
    return 'option-box option-box-1'; // fallback
  };

  // 是否已经选中了某个选项
  const isSelected = (idx) => {
    return (answers[questionId] || []).includes(idx);
  };

  // 当前题序号用于进度条高亮
  // 例如 7道题 -> 7个圆圈
  const totalQuestions = questions.length;

  return (
    <div className="quiz-page-container">

      {/* 倒计时遮罩层 */}
      {isCountingDown && (
        <div className="countdown-overlay">
          <div className="countdown-modal">
            <h3>Waiting for your result...</h3>
            <div className="countdown-number">{countdown}</div>
          </div>
        </div>
      )}

      {/* 若已生成结果，则展示结果，否则展示题目 */}
      {result ? (
        !isCountingDown && (
          <div className="result-container">
            <h3>{result.title}</h3>
            <p>{result.intro}</p>
            <div className="result-section">
              <h4>Strengths:</h4>
              <div className="result-text">
                {result.strengths.map((s, i) => (
                  <p key={i}>{s}</p>
                ))}
              </div>
            </div>
            <div className="result-section">
              <h4>Weaknesses:</h4>
              <div className="result-text">
                {result.weaknesses.map((w, i) => (
                  <p key={i}>{w}</p>
                ))}
              </div>
            </div>
            <div className="result-section">
              <h4>Ways to Improve:</h4>
              <div className="result-text">
                {result.waysToImprove.map((tip, i) => (
                  <p key={i}>{tip}</p>
                ))}
              </div>
            </div>
            <div className="post-result-buttons">
              <button
                type="button"
                className="nav-btn"
                style={{ marginRight: '15px' }}
                onClick={handleRetake}
              >
                Retake the Quiz
              </button>
              <button type="button" className="nav-btn" onClick={handleLearnMore}>
                Learn More Financial Personality
              </button>
            </div>
          </div>
        )
      ) : (
        <>

          {/* 问题号 */}
          <div className="question-number-container">
            <div className="circle-background"></div>
            <div className="circle-border"></div>
            <div className="question-number-text">
              #{currentQuestionIndex + 1}
            </div>
          </div>
          {/* 问题文本 */}
          <div className="question-text">
            {question.question}
          </div>

          {/* 根据选项数量绝对定位 */}
          {question.options.map((opt, idx) => (
            <div
              key={idx}
              className={`${getOptionPositionClass(idx)} ${
                isSelected(idx) ? 'selected-option' : ''
              }`}
              onClick={() => handleOptionClick(questionId, idx)}
            >
              <div className="option-bg"></div>
              <div className="option-text" dangerouslySetInnerHTML={{ __html: opt.option }} />
            </div>
          ))}

          {/* Back / Next / Submit 按钮 */}
          {currentQuestionIndex > 0 && (
            <div className="back-button" onClick={goToPreviousQuestion}>
              <div className="back-bg"></div>
              <div className="back-text">Back</div>
            </div>
          )}

          {/* 如果不是最后一题，显示 Next；否则显示 Submit */}
          {currentQuestionIndex < totalQuestions - 1 ? (
            <div className="next-button" onClick={goToNextQuestion}>
              <div className="next-bg"></div>
              <div className="next-text">Next</div>
            </div>
          ) : (
            <div className="next-button" onClick={handleSubmit}>
              <div className="next-bg"></div>
              <div className="next-text">Submit</div>
            </div>
          )}

          {/* 底部进度条 */}
          <div className="progress-line">
            <svg width="970" height="11" viewBox="0 0 970 11" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M0.5 5.5H969.5" stroke="black" strokeWidth="10" />
            </svg>
          </div>

          {/* 7 个进度圆（假设7道题），当前题高亮为黑底+黄边，其余蓝底+黑边 */}
          {Array.from({ length: 7 }).map((_, idx) => {
            const circleClass = `progress-circle circle-${idx + 1}`;
            const isActive = idx === currentQuestionIndex; // 当前题
            return (
              <div className={circleClass} key={idx}>
                {isActive ? (
                  <>
                    <div className="progress-inner-black"></div>
                    <div className="progress-inner-yellow"></div>
                    <div className="progress-circle-text">{idx + 1}</div>
                  </>
                ) : (
                  <>
                    <div className="progress-inner-blue"></div>
                    <div className="progress-circle-text">{idx + 1}</div>
                  </>
                )}
              </div>
            );
          })}
        </>
      )}
    </div>
  );
};

export default QuizPage;
