import React, { useState, useEffect } from 'react';
import { questions, quizResultDescriptions } from './data';
import { useNavigate } from 'react-router-dom';
import './QuizPage.css';


import squirrelImg from './images/Squirrel.png';
import penguinImg from './images/Penguin.png';
import owlImg from './images/Owl.png';
import foxImg from './images/Fox.png';
import raccoonImg from './images/Raccoon.png';
import cheetahImg from './images/Cheetah.png';
import goldenRetrieverImg from './images/GoldenRetriever.png';
import capybaraImg from './images/Capybara.png';

// 为每个动物映射对应的图片 (用于最终结果显示)
const animalImages = {
  Squirrel: squirrelImg,
  Penguin: penguinImg,
  Owl: owlImg,
  Fox: foxImg,
  Raccoon: raccoonImg,
  Cheetah: cheetahImg,
  'Golden Retriever': goldenRetrieverImg,
  Capybara: capybaraImg,
};

const QuizPage = () => {
  const navigate = useNavigate();

  // 当前题目索引
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  // 每道题可选最多2个选项：answers[questionId] = [optionIndex1, optionIndex2]
  const [answers, setAnswers] = useState({});
  // 最终结果
  const [result, setResult] = useState(null);

  // 阶段： 'quiz' | 'countdown' | 'loading' | 'result'
  const [stage, setStage] = useState('quiz');

  // 倒计时数字
  const [countdown, setCountdown] = useState(3);

  // “You are a...” 加载画面相关
  const [ellipsisCount, setEllipsisCount] = useState(0); // 用于省略号动画
  const [imageOpacity, setImageOpacity] = useState(0);   // 用于图片淡入

  // 点击选项
  const handleOptionClick = (questionId, optionIndex) => {
    const selectedOptions = answers[questionId] || [];
    // 若已选中则取消
    if (selectedOptions.includes(optionIndex)) {
      const newSelection = selectedOptions.filter((i) => i !== optionIndex);
      setAnswers({ ...answers, [questionId]: newSelection });
    } else {
      // 新选中
      if (selectedOptions.length < 2) {
        setAnswers({ ...answers, [questionId]: [...selectedOptions, optionIndex] });
      } else {
        alert('You can select at most 2 options for this question.');
      }
    }
  };

  // 下一题
  const goToNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  // 上一题
  const goToPreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  // 提交测验
  const handleSubmit = () => {
    // 1) 检查是否每题至少选1个
    for (let i = 0; i < questions.length; i++) {
      const q = questions[i];
      const selected = answers[q.id] || [];
      if (selected.length === 0) {
        alert(`Please select at least one option for question ${i + 1}`);
        return;
      }
    }

    // 2) 计算分数
    const scores = {
      Squirrel: 0,
      Penguin: 0,
      Owl: 0,
      Fox: 0,
      Raccoon: 0,
      Cheetah: 0,
      'Golden Retriever': 0,
      Capybara: 0,
    };

    questions.forEach((question) => {
      const selectedIndices = answers[question.id] || [];
      selectedIndices.forEach((idx) => {
        const selectedOption = question.options[idx];
        for (let animal in selectedOption.score) {
          scores[animal] += selectedOption.score[animal];
        }
      });
    });

    // 3) 找到最高分；并列则取最先出现的
    let finalAnimal = null;
    let highestScore = -Infinity;
    for (const animal of Object.keys(scores)) {
      if (scores[animal] > highestScore) {
        highestScore = scores[animal];
        finalAnimal = animal;
      }
    }

    // 4) 进入倒计时阶段
    setResult(quizResultDescriptions[finalAnimal]);
    setStage('countdown');
    setCountdown(3);
  };

  // 提交后进入倒计时
  useEffect(() => {
    if (stage === 'countdown') {
      const intervalId = setInterval(() => {
        setCountdown((prev) => {
          if (prev <= 1) {
            clearInterval(intervalId);
            // 倒计时结束 -> 进入加载画面
            setStage('loading');
            setEllipsisCount(0);
            setImageOpacity(0);
          }
          return prev - 1;
        });
      }, 1000);

      return () => clearInterval(intervalId);
    }
  }, [stage]);

  // “You are a...” 加载画面：3秒动画
  useEffect(() => {
    if (stage === 'loading') {
      // 省略号动画
      const ellipsisTimer = setInterval(() => {
        setEllipsisCount((c) => (c < 3 ? c + 1 : 0));
      }, 500);

      // 图片淡入
      const fadeTimer = setTimeout(() => {
        setImageOpacity(1);
      }, 1000);

      // 3秒后进入 result
      const loadingTimer = setTimeout(() => {
        clearInterval(ellipsisTimer);
        setStage('result');
      }, 3000);

      return () => {
        clearInterval(ellipsisTimer);
        clearTimeout(loadingTimer);
        clearTimeout(fadeTimer);
      };
    }
  }, [stage]);

  // 重做测验
  const handleRetake = () => {
    setAnswers({});
    setResult(null);
    setStage('quiz');
    setCurrentQuestionIndex(0);
  };

  // 去金融人格详情
  const handleLearnMore = () => {
    navigate('/personality');
  };

  // 进度圆圈数量
  const totalQuestions = questions.length;

  // 当前题目
  const question = questions[currentQuestionIndex];
  const hasFiveOptions = question.options.length === 5;

  // 计算进度条/按钮在不同选项数下的下移
  // 例如，如果有5个选项，就整体往下再移动 60px (可自行调整)
  const shiftDown = hasFiveOptions ? 100 : 0;

  // 判断选中
  const isSelected = (idx) => {
    return (answers[question.id] || []).includes(idx);
  };

  // 返回 “option-box-x” 类名，若有第5个选项，则给它单独位置
  const getOptionClass = (idx) => {
    if (idx === 0) return 'option-box option-box-1';
    if (idx === 1) return 'option-box option-box-2';
    if (idx === 2) return 'option-box option-box-3';
    if (idx === 3) return 'option-box option-box-4';
    if (idx === 4) return 'option-box option-box-5'; // 第 5 个选项
    return 'option-box option-box-1'; // fallback
  };

  // “You are a...” 阶段显示的动物图片
  const finalAnimalName = result?.title || '';
  const loadingImg = animalImages[finalAnimalName] || penguinImg;

  // 最终结果页需要显示的动物
  // 仅当 stage === 'result' 时
  const finalAnimalImg = animalImages[finalAnimalName] || penguinImg;

  // 渲染阶段
  if (stage === 'countdown') {
    // 倒计时阶段：全屏遮罩
    return (
      <div className="countdown-overlay">
        <div className="countdown-modal">
          <h3>Waiting for your result...</h3>
          <div className="countdown-number">{countdown}</div>
        </div>
      </div>
    );
  }

  if (stage === 'loading') {
    // “You are a...” 加载阶段
    const dots = '.'.repeat(ellipsisCount); // 根据 ellipsisCount 渲染省略号
    return (
      <div
        style={{
          width: '1280px',
          height: '936px',
          background: '#0057FF',
          paddingTop: '128px',
          paddingBottom: '341px',
          paddingLeft: '120px',
          paddingRight: '120px',
          display: 'inline-flex',
          flexDirection: 'column',
          justifyContent: 'flex-start',
          alignItems: 'center',
        }}
      >
        <div
          style={{
            width: '1040px',
            height: '212px',
            textAlign: 'center',
            color: 'white',
            fontSize: '128px',
            fontFamily: 'RL Aqva, sans-serif',
            fontWeight: 900,
            wordWrap: 'break-word',
          }}
        >
          You are a{dots}
        </div>
        <div
          style={{
            width: '254px',
            height: '254px',
            justifyContent: 'center',
            alignItems: 'center',
            display: 'inline-flex',
            transition: 'opacity 1s',
            opacity: imageOpacity,
          }}
        >
          <img
            style={{ width: '254px', height: '254px' }}
            src={loadingImg}
            alt="animal"
          />
        </div>
      </div>
    );
  }

  if (stage === 'result' && result) {
    // 最终结果页面（根据给出的绝对定位设计）
    return (
      <div
        style={{
          width: '1280px',
          height: '1163px',
          position: 'relative',
          background: '#0057FF',
        }}
      >
        {/* 如果有单独 Navbar，在 App.js 中，所以这里不写 */}
        {/* 右上角导航文字仅做示例 */}
        <div
          style={{
            width: '156px',
            height: '68px',
            left: '761px',
            top: '35px',
            position: 'absolute',
            textAlign: 'center',
            color: 'white',
            fontSize: '32px',
            fontFamily: 'RL Aqva, sans-serif',
            fontWeight: 900,
            wordWrap: 'break-word',
          }}
        >
          Quiz!
        </div>
        <div
          style={{
            width: '421px',
            height: '68px',
            left: '869px',
            top: '35px',
            position: 'absolute',
            textAlign: 'center',
            color: 'white',
            fontSize: '32px',
            fontFamily: 'RL Aqva, sans-serif',
            fontWeight: 900,
            wordWrap: 'break-word',
          }}
        >
          Personality Types
        </div>

        {/* 浅蓝色卡片区 */}
        <div
          style={{
            width: '489px',
            height: '660px',
            left: '395px',
            top: '128px',
            position: 'absolute',
            background: '#CFF1FF',
            borderRadius: '30px',
            border: '10px black solid',
          }}
        ></div>

        {/* 动物名字 + 副标题 */}
        <div
          style={{
            width: '300px',
            height: '50px',
            left: '424px',
            top: '150px',
            position: 'absolute',
            color: '#252525',
            fontSize: '24px',
            fontFamily: 'RL Aqva, sans-serif',
            fontWeight: 900,
            wordWrap: 'break-word',
          }}
        >
          {result.title}
        </div>
        <div
          style={{
            width: '222px',
            height: '50px',
            left: '640px',
            top: '150px',
            position: 'absolute',
            textAlign: 'right',
            color: '#252525',
            fontSize: '24px',
            fontFamily: 'RL Aqva, sans-serif',
            fontWeight: 900,
            wordWrap: 'break-word',
          }}
        >
          {result.subtitle || 'Debt Fighter'}
        </div>

        {/* 头部圆角透明背景 + 动物图片 */}
        <div
          style={{
            width: '432px',
            height: '216px',
            left: '424px',
            top: '223px',
            position: 'absolute',
            background: 'linear-gradient(0deg, rgba(0, 87, 255, 0.20) 0%, rgba(0, 87, 255, 0.20) 100%)',
            borderRadius: '30px',
            border: '10px black solid',
          }}
        ></div>
        <img
          style={{
            width: '177px',
            height: '177px',
            left: '551px',
            top: '242px',
            position: 'absolute',
          }}
          src={finalAnimalImg}
          alt={result.title}
        />

        {/* Who am I? + 描述 */}
        <div
          style={{
            width: '120px',
            height: '33px',
            left: '424px',
            top: '449px',
            position: 'absolute',
            color: '#252525',
            fontSize: '20px',
            fontFamily: 'RL Aqva, sans-serif',
            fontWeight: 900,
            wordWrap: 'break-word',
          }}
        >
          Who am I?
        </div>
        <div
          style={{
            width: '413px',
            height: '59px',
            left: '424px',
            top: '483px',
            position: 'absolute',
            color: '#252525',
            fontSize: '16px',
            fontFamily: 'Red Rose, sans-serif',
            fontWeight: 700,
            wordWrap: 'break-word',
          }}
        >
          {result.intro}
        </div>

        {/* Strength */}
        <div
          style={{
            width: '120px',
            height: '33px',
            left: '424px',
            top: '579px',
            position: 'absolute',
            color: '#252525',
            fontSize: '20px',
            fontFamily: 'RL Aqva, sans-serif',
            fontWeight: 900,
            wordWrap: 'break-word',
          }}
        >
          Strength
        </div>
        <div
          style={{
            width: '432px',
            height: '40px',
            left: '424px',
            top: '613px',
            position: 'absolute',
            color: '#252525',
            fontSize: '16px',
            fontFamily: 'Red Rose, sans-serif',
            fontWeight: 700,
            wordWrap: 'break-word',
          }}
        >
          {result.strengths.join(' ')}
        </div>

        {/* Weakness */}
        <div
          style={{
            width: '120px',
            height: '33px',
            left: '424px',
            top: '672px',
            position: 'absolute',
            color: '#252525',
            fontSize: '20px',
            fontFamily: 'RL Aqva, sans-serif',
            fontWeight: 900,
            wordWrap: 'break-word',
          }}
        >
          Weakness
        </div>
        <div
          style={{
            width: '432px',
            height: '40px',
            left: '424px',
            top: '702px',
            position: 'absolute',
            color: '#252525',
            fontSize: '16px',
            fontFamily: 'Red Rose, sans-serif',
            fontWeight: 700,
            wordWrap: 'break-word',
          }}
        >
          {result.weaknesses.join(' ')}
        </div>

        {/* “See other types” 按钮 */}
        <div
          style={{
            width: '588px',
            height: '93px',
            left: '346px',
            top: '869px',
            position: 'absolute',
            cursor: 'pointer',
          }}
          onClick={handleLearnMore}
        >
          <div
            style={{
              width: '588px',
              height: '93px',
              left: '0px',
              top: '0px',
              position: 'absolute',
              background: '#FED702',
              borderRadius: '46.5px',
              border: '10px black solid',
            }}
          ></div>
          <div
            style={{
              width: '297.82px',
              height: '44px',
              left: '145.09px',
              top: '25px',
              position: 'absolute',
              textAlign: 'center',
              color: 'black',
              fontSize: '40px',
              fontFamily: 'RL Aqva, sans-serif',
              fontWeight: 900,
              wordWrap: 'break-word',
            }}
          >
            See other types
          </div>
        </div>

        {/* “Try again?” 按钮 */}
        <div
          style={{
            width: '588px',
            height: '93px',
            left: '346px',
            top: '985px',
            position: 'absolute',
            cursor: 'pointer',
          }}
          onClick={handleRetake}
        >
          <div
            style={{
              width: '588px',
              height: '93px',
              left: '0px',
              top: '0px',
              position: 'absolute',
              background: '#E681FF',
              borderRadius: '34.88px',
              border: '7.5px black solid',
            }}
          ></div>
          <div
            style={{
              width: '297.82px',
              height: '44px',
              left: '145.09px',
              top: '25px',
              position: 'absolute',
              textAlign: 'center',
              color: 'black',
              fontSize: '30px',
              fontFamily: 'RL Aqva, sans-serif',
              fontWeight: 900,
              wordWrap: 'break-word',
            }}
          >
            Try again?
          </div>
        </div>
      </div>
    );
  }

  // 否则：测验阶段
  return (
    <div className="quiz-page-container" style={{ minHeight: 850 + shiftDown }}>
      {/* 当前题号圆 */}
      <div className="question-number-container">
        <div className="circle-background"></div>
        <div className="circle-border"></div>
        <div className="question-number-text">#{currentQuestionIndex + 1}</div>
      </div>

      {/* 问题文本 */}
      <div className="question-text">{question.question}</div>

      {/* 根据选项数量渲染 2×2 或 2×2 + 第5个 */}
      {question.options.map((opt, idx) => {
        const classes = getOptionClass(idx);
        const selectedClass = isSelected(idx) ? ' selected-option' : '';
        return (
          <div
            key={idx}
            className={classes + selectedClass}
            onClick={() => handleOptionClick(question.id, idx)}
          >
            <div className="option-bg"></div>
            <div
              className="option-text"
              dangerouslySetInnerHTML={{ __html: opt.option }}
            />
          </div>
        );
      })}

      {/* 如果不是第一题，显示 Back 按钮 */}
      {currentQuestionIndex > 0 && (
        <div
          className="back-button"
          style={{ top: 536 + shiftDown }}
          onClick={goToPreviousQuestion}
        >
          <div className="back-bg"></div>
          <div className="back-text">Back</div>
        </div>
      )}

      {/* 如果不是最后一题，显示 Next，否则 Submit */}
      {currentQuestionIndex < totalQuestions - 1 ? (
        <div
          className="next-button"
          style={{ top: 537 + shiftDown }}
          onClick={goToNextQuestion}
        >
          <div className="next-bg"></div>
          <div className="next-text">Next</div>
        </div>
      ) : (
        <div
          className="next-button"
          style={{ top: 537 + shiftDown }}
          onClick={handleSubmit}
        >
          <div className="next-bg"></div>
          <div className="next-text">Submit</div>
        </div>
      )}

      {/* 底部进度条 (下移 shiftDown) */}
      <div className="progress-line" style={{ top: 689.5 + shiftDown }}>
        <svg width="970" height="11" viewBox="0 0 970 11" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M0.5 5.5H969.5" stroke="black" strokeWidth="10" />
        </svg>
      </div>

      {/* 7 个进度圆，当前题高亮 */}
      {Array.from({ length: totalQuestions }).map((_, idx) => {
        // 若你固定7题，可写 length: 7
        // 这里用 totalQuestions 避免硬编码
        const circleClass = `progress-circle circle-${idx + 1}`;
        const isActive = idx === currentQuestionIndex;
        return (
          <div
            key={idx}
            className={circleClass}
            style={{ top: 652 + shiftDown }}
          >
            {isActive ? (
              <>
                <div className="progress-inner-black"></div>
                <div className="progress-inner-yellow"></div>
                <div className="progress-circle-text">{idx + 1}</div>
              </>
            ) : (
              <>
                <div className="progress-inner-blue"></div>
                <div className="progress-circle-text">{idx + 1}</div>
              </>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default QuizPage;
